import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import './App.scss'; // Assuming your SCSS is in App.scss

// Import Components
import HomePage from './components/HomePage';
import Login from './components/Login';
import Register from './components/Register';
import CreateFlashcardSet from './components/CreateFlashcardSet';
import FlashcardSet from './components/FlashcardSet';
import FlashcardReview from './components/FlashcardReview';

// Auth context to manage user state
import { AuthContext } from './context/AuthContext';

const App = () => {
  // State to track if the user is logged in
  const [user, setUser] = useState(null);

  // Check if user is logged in (this is a simplified approach)
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) {
      setUser(storedUser); // Set user state if available in localStorage
    }
  }, []);

  // Handle user logout
  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null); // Set user to null upon logout
  };

  return (
    <AuthContext.Provider value={{ user, setUser }}>
      <Router>
        <div className="app">
          <nav className="navbar">
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              {user ? (
                <>
                  <li>
                    <Link to="/create">Create Flashcards</Link>
                  </li>
                  <li>
                    <button onClick={handleLogout}>Logout</button>
                  </li>
                </>
              ) : (
                <>
                  <li>
                    <Link to="/login">Login</Link>
                  </li>
                  <li>
                    <Link to="/register">Register</Link>
                  </li>
                </>
              )}
            </ul>
          </nav>

          <main>
            <Switch>
              <Route exact path="/" component={HomePage} />
              <Route path="/login" component={Login} />
              <Route path="/register" component={Register} />
              <Route path="/create" component={CreateFlashcardSet} />
              <Route path="/flashcards/:setId" component={FlashcardSet} />
              <Route path="/review/:setId" component={FlashcardReview} />
            </Switch>
          </main>
        </div>
      </Router>
    </AuthContext.Provider>
  );
};

export default App;
