import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import './App.scss';

import Login from './components/Login';
import Register from './components/Register';
import CreateFlashcardSet from './components/CreateFlashcardSet';
import FlashcardReview from './components/FlashcardReview';
import HomePage from './components/HomePage';

import { AuthContext } from './context/AuthContext';

const App = () => {
  const [user, setUser] = useState(null);

  // check for user authentication status
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) {
      setUser(storedUser);
    }
  }, []);

  // handle user logout
  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, setUser }}>
      <Router>
        <div className="app">
          <nav className="navbar">
            <ul>
              {user ? (
                <>
                  <button onClick={handleLogout}>Logout</button>
                  <li>
                    <Link to="/create">Create Flashcards</Link>
                  </li>
                  <li>
                    <Link to="/review">Review Flashcards</Link>
                  </li>
                </>
              ) : (
                <>
                  <li>
                    <Link to="/login">Login</Link>
                  </li>
                  <li>
                    <Link to="/register">Register</Link>
                  </li>
                </>
              )}
            </ul>
          </nav>

          <main>
            <Routes>
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
              <Route path="/create" element={<CreateFlashcardSet />} />
              <Route path="/review" element={<FlashcardReview />} />
              {/* Redirect to login if user is not authenticated */}
              <Route path="/" element={user ? <HomePage /> : <Login />} />
            </Routes>
          </main>
        </div>
      </Router>
    </AuthContext.Provider>
  );
};

export default App;
