import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const FlashcardReview = () => {
  const [set, setSet] = useState(null);
  const [currentCardIndex, setCurrentCardIndex] = useState(0);
  const [flipped, setFlipped] = useState(false);
  const { setId } = useParams();

  useEffect(() => {
    const fetchSet = async () => {
      const response = await fetch(`/api/flashcards/${setId}`);
      const data = await response.json();
      setSet(data);
    };

    fetchSet();
  }, [setId]);

  const nextCard = () => {
    setCurrentCardIndex((prevIndex) => (prevIndex + 1) % set.cards.length);
    setFlipped(false);
  };

  const flipCard = () => {
    setFlipped((prev) => !prev);
  };

  if (!set) return <div>Loading...</div>;

  const currentCard = set.cards[currentCardIndex];

  return (
    <div className="review-mode">
      <h2>Reviewing: {set.title}</h2>
      <div className="card">
        <div className="card-content" onClick={flipCard}>
          {flipped ? <p>{currentCard.back}</p> : <p>{currentCard.front}</p>}
        </div>
        <button onClick={nextCard}>Next Card</button>
      </div>
    </div>
  );
};

export default FlashcardReview;
