import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const CreateFlashcardSet = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [cards, setCards] = useState([{ front: '', back: '' }]);
  const navigate = useNavigate();

  const addCard = () => {
    setCards([...cards, { front: '', back: '' }]);
  };

  const removeCard = (index) => {
    const updatedCards = cards.filter((_, i) => i !== index);
    if (updatedCards.length==0) {
      alert("Your study set must have at least one card!");
      return;
    }
    setCards(updatedCards);
  };

  const handleCardChange = (index, field, value) => {
    const updatedCards = [...cards];
    updatedCards[index][field] = value;
    setCards(updatedCards);
  };


  const handleSubmit = async (e) => {
    e.preventDefault();


    let userId = localStorage.getItem('userId');
    try {
      const response = await fetch('http://localhost:5050/api/flashcards/create', {
        method: 'POST',
        body: JSON.stringify({ userId, title, description, cards }),
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });

      if (!response.ok) {
        throw new Error('Failed to create flashcard set');
      }

      const data = await response.json();
      if (data._id) {
        navigate(`/review/${data._id}`);
      } else {
        alert('Error with id when creating set');
      }
    } catch (error) {
      console.error(error);
      alert('Error creating set');
    }
  };

  return (
    <div className="create-set">
      <h2>Create Flashcard Set</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          style={{width: "100%"}}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Write a description for this flashcard set"
          value={description}
          style={{width: "100%", height: 100}}
          onChange={(e) => setDescription(e.target.value)}
        />

        {cards.map((card, index) => (
          <div key={index} className="card-form" style={{width: "100%"}}>
            <input
              type="text"
              placeholder="Card front"
              value={card.front}
              style={{width: "100%"}}
              onChange={(e) => handleCardChange(index, 'front', e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="Card back"
              value={card.back}
              style={{width: "100%"}}
              onChange={(e) => handleCardChange(index, 'back', e.target.value)}
              required
            />
            <button type="button" onClick={() => removeCard(index)} style={{width: "100%"}}>Remove Card</button>
          </div>
        ))}
        <button type="button" onClick={addCard}>Add Card</button>
        <button type="submit">Create Set</button>
      </form>
    </div>
  );
};

export default CreateFlashcardSet;
