// /client/src/components/FlashcardSet.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const FlashcardSet = () => {
  const [set, setSet] = useState(null);
  const [loading, setLoading] = useState(true);
  const { setId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchSet = async () => {
      const response = await fetch(`/api/flashcards/${setId}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });
      const data = await response.json();
      setSet(data);
      setLoading(false);
    };

    fetchSet();
  }, [setId]);

  const handleDeleteCard = async (cardId) => {
    const response = await fetch(`/api/flashcards/delete/${setId}/${cardId}`, {
      method: 'DELETE',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`,
      },
    });
    const data = await response.json();
    if (data.message === 'Flashcard deleted') {
      setSet(prevSet => ({
        ...prevSet,
        cards: prevSet.cards.filter(card => card._id !== cardId),
      }));
    }
  };

  const handleFavoriteCard = async (cardId) => {
    const response = await fetch(`/api/flashcards/favorite/${setId}/${cardId}`, {
      method: 'PUT',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`,
      },
    });
    const data = await response.json();
    if (data._id) {
      setSet(prevSet => ({
        ...prevSet,
        cards: prevSet.cards.map(card =>
          card._id === cardId ? { ...card, isFavorite: !card.isFavorite } : card
        ),
      }));
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="flashcard-set">
      <h2>{set.title}</h2>
      <p>{set.description}</p>
      <div className="cards">
        {set.cards.map(card => (
          <div key={card._id} className="flashcard">
            <p>{card.front} - {card.back}</p>
            <button onClick={() => handleFavoriteCard(card._id)}>
              {card.isFavorite ? 'Unfavorite' : 'Favorite'}
            </button>
            <button onClick={() => handleDeleteCard(card._id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FlashcardSet;
