import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { setUser } = React.useContext(AuthContext);
  const navigate = useNavigate();

  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    // // FIX: replace with actual API call
    // const userData = { email };  // user data should contain email
    // localStorage.setItem('user', JSON.stringify(userData)); // store in localStorage
    // setUser(userData); // update the context state

    // navigate('/'); // redirect to HomePage after login
    try {
      const response = await fetch('http://localhost:5050/api/auth/login', {
        method: 'POST',
        body: JSON.stringify({ email, password }),
        headers: { 'Content-Type': 'application/json' },
      });

      const data = await response.json();
      if (!response.ok) {
        // If the response is not OK (i.e., status code is not 200)
        setError(data.error || 'Login failed');
        console.error('Login error:', data);
        return;
      }

      if (response.ok) {
        // Store the JWT token and user details in localStorage
        localStorage.setItem('token', data.token);
        localStorage.setItem('userId', data.userId);
        const userData = { email }; // You can include other user details if needed
        localStorage.setItem('user', JSON.stringify(userData));

        // Update the AuthContext
        setUser(userData);

        // Redirect to HomePage
        navigate('/');
      } else {
        // Handle error if login fails
        setError(data.error || 'Login failed');
      }
    } catch (err) {
      // Handle network or unexpected errors
      setError('An error occurred. Please try again.');
    }
  };

  return (
    <div className="login-page">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
