import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const CreateFlashcardSet = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [cards, setCards] = useState([{ front: '', back: '' }]);
  const navigate = useNavigate();

  const addCard = () => {
    setCards([...cards, { front: '', back: '' }]);
  };

  const removeCard = (index) => {
    const updatedCards = cards.filter((_, i) => i !== index);
    setCards(updatedCards);
  };

  const handleCardChange = (index, field, value) => {
    const updatedCards = [...cards];
    updatedCards[index][field] = value;
    setCards(updatedCards);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('/api/flashcards/create', {
      method: 'POST',
      body: JSON.stringify({ title, description, cards }),
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('token')}`,
      },
    });
    const data = await response.json();
    if (data._id) {
      navigate(`/flashcards/${data._id}`);
    } else {
      alert('Error creating set');
    }
  };

  return (
    <div className="create-set">
      <h2>Create Flashcard Set</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Set Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Set Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <button type="button" onClick={addCard}>Add Card</button>
        {cards.map((card, index) => (
          <div key={index} className="card-form">
            <input
              type="text"
              placeholder="Card Front"
              value={card.front}
              onChange={(e) => handleCardChange(index, 'front', e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="Card Back"
              value={card.back}
              onChange={(e) => handleCardChange(index, 'back', e.target.value)}
              required
            />
            <button type="button" onClick={() => removeCard(index)}>Remove Card</button>
          </div>
        ))}
        <button type="submit">Create Set</button>
      </form>
    </div>
  );
};

export default CreateFlashcardSet;
